insert into Language_en_US (Tag, Text)
values ('TXT_KEY_DECISIONS_CLWABANAKICONVERSION_NO_NAME', 'Declare a foreign religion to be the truth'),
	   ('TXT_KEY_DECISIONS_CLWABANAKICONVERSION_NO_DESC', 'Our leader, Henri Membertou, believes that the next revelation of our beliefs will come from across the seas. We shall embrace this new belief as our own![NEWLINE]Requirement/Restrictions:[NEWLINE][ICON_BULLET]Player must be the Wabanaki[NEWLINE][ICON_BULLET]At least one follower of a foreign religion in one of your cities.[NEWLINE][ICON_BULLET]May only be enacted once per game.[NEWLINE]Costs:[NEWLINE][ICON_BULLET]1 [ICON_MAGISTRATES] Magistrate[NEWLINE][ICON_BULLET]All of your population converts to the foreign religion.[NEWLINE]Rewards:[NEWLINE][ICON_BULLET]Whenever the founder of the foreign religion gains a Social Policy, gain one third of that Policy''s cost in [ICON_CULTURE] Culture.[NEWLINE][ICON_BULLET]Whenever the founder of the foreign religion gains a Great Person, gain a surge of [ICON_PEACE] Faith.'),
	   ('TXT_KEY_DECISIONS_CLWABANAKICONVERSION_NAME', 'Declare {1_Rel} to be the truth.'),
